mapper.select:
SELECT album_id, friend_group_id, create_time, update_time
FROM album_access
WHERE friend_group_id = #{friendGroupId} AND album_id = #{albumId}

mapper.delete:
DELETE FROM album_access
WHERE album_id = #{albumId}
AND friend_group_id IN

    #{friendGroupId}

mapper.select:
SELECT EXISTS(
    SELECT * FROM album_access, friendship
    WHERE album_access.friend_group_id = friendship.friend_group_id
    AND album_id = #{albumId}
    AND friendship.friend_user_id = #{userId}
) AS canAccess
