mapper.select:
select id, album_name, owner, draft_group, is_public, secret_key, create_time, update_time from album
where owner = #{userId}
limit #{start}, #{count}

select id, album_name, owner, draft_group, is_public, secret_key, create_time, update_time from album
where owner = (select id from user where username = #{username})
limit #{start}, #{count}

mapper.update:
update album
set draft_group = #{pictureGroupId}
where id = #{albumId}
