mapper.select:
SELECT id, picture_group_id, picture_format, filepath, sequence, create_time, update_time
FROM pictureRef
WHERE id = #{id}

SELECT filepath
FROM pictureRef
WHERE id = #{pictureId} AND picture_group_id = #{pictureGroupId}

mapper.update:
UPDATE pictureRef
SET sequence = #{sequence}
WHERE id = #{pictureId} AND picture_group_id = #{pictureGroupId}
